import React, { useState } from 'react';
import { Typography, TextField, Button, Container, Box, Grid, Paper } from 'mui/material';
import { useNavigate } from 'react-router-dom'; // Import useNavigate from react-router-dom

const Login = ({ setAccessToken }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [errors, setErrors] = useState('');

  // Access the navigate function
  const navigate = useNavigate();

  const handleLogin = () => {
    // Assume successful login for simplicity
    // In a real application, you would perform authentication and handle errors
    setAccessToken('fakeAccessToken'); // Set a fake access token for demonstration

    // Redirect to the ReceiptForm page after successful login
    navigate('/receipt-form');
  };

  return (
    <Container maxWidth="lg">
      <Box
        sx={{
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          justifyContent: 'center',
          minHeight: '100vh',
          backgroundColor: '#f7faff',
        }}
      >
        <Paper elevation={3} sx={{ p: 4, backgroundColor: 'white' }}>
          <Grid container spacing={2}>
            <img
                src="https://cat.sn/storage/0XFJUqtbNQwEZwYXiSSMt6KJLWRTPUHMqA81frjc.png"
                alt="Logo"
                style={{ width: '100%', height: 'auto' }}
              />
            <Grid item xs={12} sm={6}>
              <Typography variant="h4" gutterBottom>
                Connexion
              </Typography>
              <div>
                <form onSubmit={(e) => e.preventDefault()}>
                  <TextField
                    type="email"
                    label="Email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    autoComplete="email"
                    fullWidth
                    required
                    margin="normal"
                  />
                  <TextField
                    type="password"
                    label="Password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    autoComplete="current-password"
                    fullWidth
                    required
                    margin="normal"
                  />
                  <Button variant="contained" color="primary" onClick={handleLogin}>
                    Login
                  </Button>
                </form>
                {errors && <Typography color="error">{errors}</Typography>}
              </div>
            </Grid>
          </Grid>
        </Paper>
      </Box>
    </Container>
  );
};

export default Login;
