import React, { useState } from 'react';

const ReceiptForm = ({ onSave }) => {
  const [formData, setFormData] = useState({
    date: '',
    name: '',
    amount: '',
    paymentType: 'Espèce',
    dossierNumber: '',
    phoneNumber: '',
    paymentReason: '',
    classe: '',
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSaveClick = () => {
    onSave(formData);
  };

  return (
    <>
      <div
        className="container mt-3"
        style={{
          display: 'flex',
          flexDirection: 'column', // Set flex direction to column
          alignItems: 'center', // Center items horizontally
          maxWidth: '1000px',
          margin: 'auto',
          marginTop: '10vh',
          paddingBottom: '20vh',
          boxSizing: 'border-box',
        }}
      >
        <h1 className="text-center">Générateur de Reçus de Paiement</h1>
        <div
          style={{
            display: 'flex',
            flexDirection: 'row', // Set flex direction to row
            justifyContent: 'center', // Center items horizontally
            marginTop: '20px', // Add some space between heading and content
          }}
        >
          <div style={{ flex: 1 }}>
            {/* Left side - Image */}
            <img
              src="https://cat.sn/storage/0XFJUqtbNQwEZwYXiSSMt6KJLWRTPUHMqA81frjc.png"
              style={{ width: '100%', height: 'auto' }}
              alt="Cat Image"
            />
          </div>
          <div style={{ flex: 1, paddingLeft: '20px' }}>
            {/* Right side - Form */}
            <form>
              {/* ... (your form code) */}
            </form>
          </div>
        </div>
      </div>
      <br />
    </>
  );
};

export default ReceiptForm;
