import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

const ReceiptDetails = () => {
  const [receiptData, setReceiptData] = useState(null);
  const { id } = useParams();

  useEffect(() => {
    const fetchReceiptById = async () => {
      try {
        // Fetch selected receipt by ID
        const response = await axios.get(`http://localhost:8000/api/receipts/${id}`);
        setReceiptData(response.data.receipt);
      } catch (error) {
        console.error('Erreur lors de la récupération des données du reçu:', error);
      }
    };

    fetchReceiptById();
  }, [id]);

  if (!receiptData) {
    // Vous pouvez afficher un indicateur de chargement ici si nécessaire
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h1>Receipt Details</h1>
      <div>
        <strong>Date:</strong> {receiptData.date}
      </div>
      <div>
        <strong>Nom Complet:</strong> {receiptData.nomComplet}
      </div>
      <div>
        <strong>Amount:</strong> {receiptData.amount} FCFA
      </div>
      {/* Ajoutez d'autres champs du reçu ici */}
    </div>
  );
};

export default ReceiptDetails;
