import React, { useState } from 'react';

const Login = ({ onLogin }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [errors, setErrors] = useState('');

  const handleLogin = async () => {
    try {
      // Simulating a successful login without a real API call

      // Replace this with your actual login logic
      // For simplicity, hardcoded credentials are used
      const expectedLogin = "comptaCAT";
      const expectedPassword = "Cat@2023";

      if (email === expectedLogin && password === expectedPassword) {
        // Login successful
        onLogin();

        // Clear form fields and errors
        setEmail("");
        setPassword("");
        setErrors("");
      } else {
        // Login failed
        setErrors("Invalid login credentials");
      }
    } catch (error) {
      console.error("An error occurred:", error);
      setErrors("Failed to login");
    }
  };

  return (
    <div className="container" style={{ display: 'flex', maxWidth: '800px', margin: 'auto', marginTop: '10vh' }}>
      <div style={{ flex: 1 }}>
        <img
          src="https://cat.sn/storage/0XFJUqtbNQwEZwYXiSSMt6KJLWRTPUHMqA81frjc.png"
          alt="Logo"
          className="img-fluid"
        />
      </div>
      <div style={{ flex: 1,  }}>
        <div className="text-center mb-4">
          <h2 className="mt-3">Connexion</h2>
        </div>
        <form>
          <div className="mb-3">
            <label htmlFor="login" className="form-label">
              Login
            </label>
            <input
              type="text"
              className="form-control"
              id="login"
              placeholder="Enter login"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>

          <div className="mb-3">
            <label htmlFor="password" className="form-label">
              Password
            </label>
            <input
              type="password"
              className="form-control"
              id="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>

          <button type="button" className="btn btn-primary btn-block" onClick={handleLogin}>
            Login
          </button>
        </form>

        {errors && <p className="text-danger mt-3">{errors}</p>}
      </div>
    </div>
  );
};

export default Login;
