import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Redirect } from 'react-router-dom';
import ReceiptForm from './Components/ReceiptForm';
import Receipt from './Components/Receipt';
import Login from './Components/Auth/Login';
import Receipts from './Components/Receipts';

function ReceiptApp() {
  const [receiptData, setReceiptData] = useState(null);
  const [isLoggedIn, setLoggedIn] = useState(false);

  const handleLogin = () => {
    // Implement your login logic here
    // For now, let's assume the login is successful
    setLoggedIn(true);
  };

  const handleSave = (data) => {
    // Store the data in the application state
    setReceiptData(data);
  };

  return (
    <Router>
      <div className="App">
        <Routes>
          <Route path="/login" element={!isLoggedIn ? <Login onLogin={handleLogin} /> : <Navigate to="/receipts" />} />
          <Route path="/receipts" element={isLoggedIn ? <Receipts /> : <Navigate to="/login" />} />
          <Route
            path="/receiptform"
            element={
              isLoggedIn && receiptData ? (
                <Receipt data={receiptData} />
              ) : isLoggedIn ? (
                <ReceiptForm onSave={handleSave} />
              ) : null
            }
          />
          {/* Add other routes as needed */}
        </Routes>
      </div>
    </Router>
  );
}

export default ReceiptApp;
