import React, { useState } from 'react';

const Login = ({ onLogin }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [errors, setErrors] = useState('');

  const buttonColor = '#007bff'; // Change this to the color of your button

  const handleLogin = async () => {
    try {
      // Simulating a successful login without a real API call

      // Replace this with your actual login logic
      // For simplicity, hardcoded credentials are used
      const expectedLogin = "comptaCAT";
      const expectedPassword = "Cat@2023";

      if (email === expectedLogin && password === expectedPassword) {
        // Login successful
        onLogin();

        // Clear form fields and errors
        setEmail("");
        setPassword("");
        setErrors("");
      } else {
        // Login failed
        setErrors("Invalid login credentials");
      }
    } catch (error) {
      console.error("An error occurred:", error);
      setErrors("Failed to login");
    }
  };

  return (
    <div
      className="container"
      style={{
        maxWidth: '100%',
        margin: 'auto',
        marginTop: '10vh',
        paddingBottom: '20vh',
        boxSizing: 'border-box',
        border: `2px solid ${buttonColor}`, // Set border color to the button color
      }}
    >
      <div className="row justify-content-md-center">
        <div className="col-md-6">
          <img
            src="https://cat.sn/storage/0XFJUqtbNQwEZwYXiSSMt6KJLWRTPUHMqA81frjc.png"
            alt="Logo"
            className="img-fluid"
          />
        </div>
        <div className="col-md-6">
          <h4>Connexion</h4>
          <form>
            <div className="mb-3">
              <label htmlFor="login" className="form-label">
                Login
              </label>
              <input
                type="text"
                className="form-control"
                id="login"
                placeholder="Enter login"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
              />
            </div>

            <div className="mb-3">
              <label htmlFor="password" className="form-label">
                Password
              </label>
              <input
                type="password"
                className="form-control"
                id="password"
                placeholder="Password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
            </div>

            <button
              type="button"
              className="btn btn-primary"
              onClick={handleLogin}
              style={{ backgroundColor: buttonColor }} // Set button background color
            >
              Login
            </button>
          </form>
          {errors && <p style={{ color: 'red' }}>{errors}</p>}
        </div>
      </div>
    </div>
  );
};

export default Login;
