import React, { useState } from 'react';
import ReceiptForm from './Components/ReceiptForm';
import Receipt from './Components/Receipt';
import Login from './Components/Auth/Login';

function ReceiptApp() {
  const [receiptData, setReceiptData] = useState(null);
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const handleSave = (data) => {
    // Stockez les données dans l'état de l'application
    setReceiptData(data);
  };

  const handleLogin = () => {
    // Mettez à jour l'état pour indiquer que l'utilisateur est connecté
    setIsLoggedIn(true);
  };

  return (
    <div className="App">
        <p>Connectez-vous pour accéder au formulaire de reçu.</p>

      {!isLoggedIn && <Login onLogin={handleLogin} />}
      {isLoggedIn ? (
        <ReceiptForm onSave={handleSave} />
      ) : (
        <p>Connexion Reussie</p>

      )}
      {receiptData && <Receipt data={receiptData} />}
    </div>
  );
}

export default ReceiptApp;
