import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import ReceiptForm from './Components/ReceiptForm';
import Receipt from './Components/Receipt';
import Login from './Components/Auth/Login';
import Receipts from './Components/Receipts';

function ReceiptApp({ onLogin }) {
  const [receiptData, setReceiptData] = useState(null);
  const [isLoggedIn, setLoggedIn] = useState(false);
  const [errors, setErrors] = useState('');

  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = () => {
    try {
      // Simulating a successful login without a real API call

      // Replace this with your actual login logic
      // For simplicity, hardcoded credentials are used
      const expectedLogin = "comptaCAT";
      const expectedPassword = "Cat@2023";

      if (email === expectedLogin && password === expectedPassword) {
        // Login successful
        onLogin();

        // Clear form fields and errors
        setEmail("");
        setPassword("");
        setErrors("");

        // Redirection vers la page receipts
        return <Navigate to="/receipts" />;
      } else {
        // Login failed
        setErrors("Login Incorrect");
      }
    } catch (error) {
      console.error("An error occurred:", error);
      setErrors("Failed to login");
    }
  };

  const handleSave = (data) => {
    // Store the data in the application state
    setReceiptData(data);
  };

  return (
    <Router>
      <div className="App">
        <Routes>
          <Route path="/" element={<Login onLogin={handleLogin} />} />
          <Route
            path="/receipts"
            element={isLoggedIn ? <Receipts /> : <Navigate to="/login" />}
          />
          <Route
            path="/receiptform"
            element={
              isLoggedIn && receiptData ? (
                <Receipt data={receiptData} />
              ) : isLoggedIn ? (
                <ReceiptForm onSave={handleSave} />
              ) : null
            }
          />
          {/* Add other routes as needed */}
        </Routes>
      </div>
    </Router>
  );
}

export default ReceiptApp;
