import React, { useState } from 'react';

const Login = ({ onLogin }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [errors, setErrors] = useState('');

  const handleLogin = async () => {
    try {
      // Simulating a successful login without a real API call
  
      // Replace this with your actual login logic
      // For simplicity, hardcoded credentials are used
      const expectedLogin = "comptaCAT";
      const expectedPassword = "Cat@2023";
  
      if (email === expectedLogin && password === expectedPassword) {
        // Login successful
        onLogin();
  
        // Clear form fields and errors
        setEmail("");
        setPassword("");
        setErrors("");
      } else {
        // Login failed
        setErrors("Invalid login credentials");
      }
    } catch (error) {
      console.error("An error occurred:", error);
      setErrors("Failed to login");
    }
  };
  

  return (
    <div className="container"style={{
      maxWidth: '700%',
      margin: 'auto',
      marginTop: '10vh', // Adjusted to use viewport height (vh) unit
      paddingBottom: '20vh', // Added padding to ensure content fits within viewport height
      boxSizing: 'border-box', // Ensures padding is included in the total height
    }}>
      <div className="row justify-content-md-center">
        <div className="col-md-6">
          <img
            src="https://cat.sn/storage/0XFJUqtbNQwEZwYXiSSMt6KJLWRTPUHMqA81frjc.png"
            alt="Logo"
            className="img-fluid"
          />
        </div>
        <div className="col-md-6">
          <h4>Connexion</h4>
          <form>
            <div className="mb-3">
            <label htmlFor="login" className="form-label">
                Login
              </label>
              <input
                type="text" // Assuming login is a string, you might want to change the type accordingly
                className="form-control"
                id="login"
                placeholder="Enter login"
                value={email} // Using email state for simplicity, you might want to rename it to login
                onChange={(e) => setEmail(e.target.value)} // Keep the setEmail function as is
                required
              />
            </div>

            <div className="mb-3">
              <label htmlFor="password" className="form-label">
                Password
              </label>
              <input
                type="password"
                className="form-control"
                id="password"
                placeholder="Password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
            </div>

            <button
              type="button"
              className="btn btn-primary"
              onClick={handleLogin}
            >
              Login
            </button>
          </form>
          {errors && <p style={{ color: 'red' }}>{errors}</p>}
        </div>
      </div>
    </div>
  );
};

export default Login;
