import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Switch, Redirect } from 'react-router-dom';
import ReceiptForm from './Components/ReceiptForm';
import Receipt from './Components/Receipt';
import Login from './Components/Auth/Login';

function ReceiptApp() {
  const [receiptData, setReceiptData] = useState(null);

  const handleSave = (data) => {
    // Store the data in the application state
    setReceiptData(data);
  };

  return (
    <Router>
      <Switch>
        <Route path="/login" component={Login} />
        <Route path="/receipt-form">
          {receiptData ? <Redirect to="/receipt" /> : <ReceiptForm onSave={handleSave} />}
        </Route>
        <Route path="/receipt">
          {receiptData ? <Receipt data={receiptData} /> : <Redirect to="/receipt-form" />}
        </Route>
        <Redirect from="/" to="/receipt-form" />
      </Switch>
    </Router>
  );
}

export default ReceiptApp;
