import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link, NavLink } from 'react-router-dom';

const Receipts = () => {
  const [receipts, setReceipts] = useState([]);

  useEffect(() => {
    // Fetch receipts when the component mounts
    fetchReceipts();
  }, []);

  const fetchReceipts = async () => {
    try {
      // Fetch all receipts from the backend
      const response = await axios.get('http://localhost:8000/api/receipts');
      setReceipts(response.data.receipts);
    } catch (error) {
      console.error('Error fetching receipts:', error.response ? error.response.data : error.message);
    }
  };

  const handleEdit = (id) => {
    // Implement the logic to navigate to the edit page or show a modal for editing
    console.log(`Editing receipt with ID ${id}`);
  };

  const handleDelete = async (id) => {
    try {
      // Send a DELETE request to delete the receipt
      await axios.delete(`http://localhost:8000/api/receipts/${id}`);
      // Update the local state after successful deletion
      setReceipts((prevReceipts) => prevReceipts.filter((receipt) => receipt.id !== id));
    } catch (error) {
      console.error('Error deleting receipt:', error.response ? error.response.data : error.message);
    }
  };

  return (
    <>
    <div className="container mt-4">
      <h1 className="mb-4">Receipts</h1>
      <div className="py-5 w-100" style={{ position: 'relative', backgroundColor: '#284081' }}>
        <Link> to="/receiptform" className="btn btn-primary" style={{ position: 'absolute', top: '10px', right: '10px' }}>
          Enregistrer
          </Link>
      </div>
      <table className="table">
        <thead>
          <tr>
            <th scope="col">ID</th>
            <th scope="col">Nom Complet</th>
            <th scope="col">Date</th>
            <th scope="col">Amount</th>
            <th scope="col">Actions</th>
          </tr>
        </thead>
        <tbody>
          {receipts.map((receipt) => (
            <tr key={receipt.id}>
              <th scope="row">{receipt.id}</th>
              <td>{receipt.nomComplet}</td>
              <td>{receipt.date}</td>
              <td>{receipt.amount}</td>
              <td>
                <button
                  type="button"
                  className="btn btn-warning btn-sm"
                  onClick={() => handleEdit(receipt.id)}
                >
                  Edit
                </button>
                <button
                  type="button"
                  className="btn btn-danger btn-sm ml-2"
                  onClick={() => handleDelete(receipt.id)}
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
    </>
  );
};

export default Receipts;
