// ReceiptApp.js
import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import ReceiptForm from './Components/ReceiptForm';
import Receipt from './Components/Receipt';
import Login from './Components/Auth/Login';
import Receipts from './Components/Receipts';

function ReceiptApp() {
  const [receiptData, setReceiptData] = useState(null);
  const [isLoggedIn, setLoggedIn] = useState(false);

  const handleLogin = () => {
    // Implémentez votre logique de connexion ici
    // Pour l'instant, supposons que la connexion est réussie
    setLoggedIn(true);

    // Redirigez vers la page des reçus une fois la connexion réussie
    Navigate('/receipts');
  };


  const handleSave = (data) => {
    // Stockez les données dans l'état de l'application
    setReceiptData(data);
  };

  return (
    <Router>
      <div className="App">
        <Routes>
          <Route
            path="/"
            element={<Login onLogin={handleLogin} />}
          />
          <Route
            path="/receipts"
            element={isLoggedIn ? <Receipts /> : <Navigate to="/login" />}
          />
          <Route
            path="/receiptform"
            element={
              isLoggedIn && receiptData ? (
                <Receipt data={receiptData} />
              ) : isLoggedIn ? (
                <ReceiptForm onSave={handleSave} />
              ) : null
            }
          />
          {/* Ajoutez d'autres routes au besoin */}
        </Routes>
      </div>
    </Router>
  );
}

export default ReceiptApp;
