import React, { useState } from 'react';
import { Modal, Button, Form } from 'react-bootstrap';

const EditReceiptModal = ({ receiptData, onSave, onClose }) => {
  const [editedData, setEditedData] = useState({ ...receiptData });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setEditedData((prevData) => ({ ...prevData, [name]: value }));
  };

  const handleSave = () => {
    // Call the onSave function to update the receipt data
    onSave(editedData);
    // Close the modal
    onClose();
  };

  return (
    <Modal show={true} onHide={onClose}>
      <Modal.Header closeButton>
        <Modal.Title>Edit Receipt</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Form>
          {/* Include form fields for editing, e.g., */}
          <Form.Group controlId="formNomComplet">
            <Form.Label>Nom Complet</Form.Label>
            <Form.Control
              type="text"
              placeholder="Enter Nom Complet"
              name="nomComplet"
              value={editedData.nomComplet}
              onChange={handleChange}
            />
          </Form.Group>

          {/* Add other form fields as needed */}
        </Form>
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={onClose}>
          Close
        </Button>
        <Button variant="primary" onClick={handleSave}>
          Save Changes
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

export default EditReceiptModal;
