import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Receipts = () => {
  const [receipts, setReceipts] = useState([]);

  useEffect(() => {
    // Fetch receipts when the component mounts
    fetchReceipts();
  }, []);

  const fetchReceipts = async () => {
    try {
      // Fetch all receipts from the backend
      const response = await axios.get('http://localhost:8000/api/receipts');
      setReceipts(response.data.receipts);
    } catch (error) {
      console.error('Error fetching receipts:', error.response ? error.response.data : error.message);
    }
  };

  const handleEdit = async (id) => {
    try {
      // Fetch the receipt data for the specific ID
      const response = await axios.get(`http://localhost:8000/api/receipts/${id}`);
      const receiptData = response.data.receipt;

      // Assume you have a route for editing receipts, update the path accordingly
      navigate(`/edit-receipt/${id}`, { state: { receiptData } });
    } catch (error) {
      console.error('Error fetching receipt for editing:', error.response ? error.response.data : error.message);
    }
  };

  const handleDelete = async (id) => {
    try {
      // Send a DELETE request to delete the receipt
      await axios.delete(`http://localhost:8000/api/delete/${id}`);
      // Update the local state after successful deletion
      setReceipts((prevReceipts) => prevReceipts.filter((receipt) => receipt.id !== id));
    } catch (error) {
      console.error('Error deleting receipt:', error.response ? error.response.data : error.message);
    }
  };

  return (
    <div className="container mt-4">
      <h1 className="mb-4">Receipts</h1>
      <table className="table">
        <thead>
          <tr>
            <th scope="col">ID</th>
            <th scope="col">Nom Complet</th>
            <th scope="col">Date</th>
            <th scope="col">Amount</th>
            <th scope="col">Type de Paiement</th>
            <th scope="col">Actions</th>
          </tr>
        </thead>
        <tbody>
          {receipts.map((receipt) => (
            <tr key={receipt.id}>
              <th scope="row">{receipt.id}</th>
              <td>{receipt.nomComplet}</td>
              <td>{receipt.date}</td>
              <td>{receipt.amount}</td>
              <td>{receipt.paymentType}</td>

              <td>
                <button
                  type="button"
                  className="btn btn-warning btn-sm"
                  onClick={() => handleEdit(receipt.id)}
                >
                  Edit
                </button>
                <button
                  type="button"
                  className="btn btn-danger btn-sm ml-2"
                  onClick={() => handleDelete(receipt.id)}
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Receipts;
